function drawNewImage(ww, hh, canvas, text = window.newImage) {
    if (text) {
        const c = canvas.getContext("2d");
        canvas.width = ww;
        canvas.height = hh;
        c.beginPath();
        c.clearRect(0,0,ww, hh);
        c.fillStyle = "white";
        c.fillRect(0, 0, ww, hh)
        const a = text.split(";");
        const w = a[0];
        const h = a[1].split(",")[0];
        const str = a[1].split(",");
        str.shift();
        const pixelWidth = ww/w;
        let i = -1;
        for (let y = 0; y < h; y++) {
            for (let x = 0; x < w; x++) {
                i++;
                drawPixel(x, y, pixelWidth, c, Number(str[i]));
            }
        }
        c.closePath();
    }
}

